// Minimal carousel functionality
class BulmaCarousel {
  constructor(element) {
    this.element = element;
    this.items = element.querySelectorAll('.carousel-item');
    this.currentIndex = 0;
    this.init();
  }

  init() {
    if (this.items.length > 0) {
      this.showItem(0);
    }
  }

  showItem(index) {
    this.items.forEach(item => item.classList.remove('is-active'));
    if (this.items[index]) {
      this.items[index].classList.add('is-active');
      this.currentIndex = index;
    }
  }

  next() {
    const nextIndex = (this.currentIndex + 1) % this.items.length;
    this.showItem(nextIndex);
  }

  prev() {
    const prevIndex = (this.currentIndex - 1 + this.items.length) % this.items.length;
    this.showItem(prevIndex);
  }
}

// Initialize carousels when DOM is loaded
document.addEventListener('DOMContentLoaded', () => {
  document.querySelectorAll('.carousel').forEach(carousel => {
    new BulmaCarousel(carousel);
  });
});